import React, { Component } from "react";
import UserTableRow from "./UserTableRow";

class NewMatch extends Component {
  state = { users: null };

  componentDidMount() {
    fetch("https://randomuser.me/api/1.1/?results=15")
      .then((response) => response.json())
      .then((data) => {
        this.setState({ users: data.results });
      });
  }

  render() {
    const { users } = this.state;
    const isLoading = users === null;
    return (
      <main>
        <div className="table-container">
          <div className="uk-overflow-auto">
            <table className="uk-table uk-table-hover uk-table-middle uk-table-divider">
              <thead>
                <tr>
                  <th className="uk-table-shrink uk-text-white" />
                  <th className="uk-table-shrink text-white" />
                  <th className="uk-table-shrink text-white">Avatar</th>
                  <th>Fullname</th>
                  <th>City</th>
                  <th>Registered</th>
                </tr>
              </thead>
              <tbody>
                {isLoading ? (
                  <tr>
                    <td colSpan={6} className="uk-text-center text-white">
                      <em className="uk-text-muted text-white">Loading...</em>
                    </td>
                  </tr>
                ) : (
                  users.map((user, index) => (
                    <UserTableRow key={index} index={index + 1} user={user} />
                  ))
                )}
              </tbody>
            </table>
          </div>
        </div>
      </main>
    );
  }
}

export default NewMatch;
