import React, { Component } from "react";

class ChangePhoto extends Component {
  constructor(props) {
    super(props);
    this.state = {
      file: "https://bootdey.com/img/Content/avatar/avatar7.png",
    };
    this.handleChange = this.handleChange.bind(this);
  }

  handleChange(event) {
    this.setState({
      file: URL.createObjectURL(event.target.files[0]),
    });
  }

  render() {
    return (
      <div>
        {" "}
        <br></br>
        <div className="card col-md-6 offset-md-3">
          <br />
          <br />
          <h3 className="text-center text-dark"> Change Your Profile Photo</h3>
          <div className="card-body text-center">
            <input type="file" onChange={this.handleChange} />
            <br />
            <br />
            <img src={this.state.file} className="rounded-circle" width="150" />
            <br />
            <br />
            <button
              style={{ marginLeft: "10px" }}
              onClick={this.backtoDashboardService}
              className="btn btn-info"
            >
              Change Photo{" "}
            </button>
            <button
              style={{ marginLeft: "10px" }}
              onClick={this.deleteaccount}
              className="btn btn-danger"
            >
              Cancle{" "}
            </button>
          </div>
        </div>
      </div>
    );
  }
}

export default ChangePhoto;
