import React, { Component } from "react";
import UserTableRow from "./UserTableRow";
import { Table, Card } from "react-bootstrap";
import "../assets/css/Style.css";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faList } from "@fortawesome/free-solid-svg-icons";

class NewMatch extends Component {
  constructor(props) {
    super(props);
    this.state = {
      trips: [
        {
          trip: {
            description:
              "Travelling with preeti and other friends. Going for Kheerganga trek, and a bike trip near kasol",
            groupSize: "5",
            tripBudget: "10000",
            tripLength: "6",
            tripDate: "2021/05/21",
            destination: "Kasol",
            landscape: "Hilly/Mountain",
            tripId: 1,
            user: {
              firstName: "Kabir",
              lastName: "Singh",
              age: "28",
              gender: "male",
            },
          },
        },
        {
          trip: {
            description:
              "Parasailing, Scuba diving & wind surfing. Staying in hostels",
            groupSize: "3",
            tripBudget: "8000",
            tripLength: "4",
            tripDate: "2021/05/21",
            destination: "Goa",
            landscape: "Beach",
            tripId: 2,
            user: {
              firstName: "Subba",
              lastName: "Reddy",
              age: "23",
              gender: "male",
            },
          },
        },
      ],
    };
  }

  render() {
    const { trips } = this.state;

    const isLoading = trips === null;
    return (
      <div>
        <Card className={"border border-dark bg-dark text-white"}>
          <Card.Header>
            <div style={{ float: "left" }}>
              <FontAwesomeIcon icon={faList} /> Trip List
            </div>
          </Card.Header>
          <div className="uk-overflow-auto">
            <Card.Body>
              <Table bordered hover striped variant="dark">
                <thead>
                  <tr>
                    <th>Destination</th>
                    <th>Date</th>
                    <th>"Trip Host</th>
                  </tr>
                </thead>
                <tbody>
                  {isLoading ? (
                    <tr>
                      <td colSpan={6} className="uk-text-center text-dark">
                        <em className="uk-text-muted text-white">Loading...</em>
                      </td>
                    </tr>
                  ) : (
                    trips.map((mtrip) => (
                      <UserTableRow key={mtrip.trip.tripId} trip={mtrip.trip} />
                    ))
                  )}
                </tbody>
              </Table>
            </Card.Body>
          </div>
        </Card>
      </div>
    );
  }
}

export default NewMatch;
