import React, { Component } from "react";
import TripServices from "../services/TripServices";
import "./../assets/css/Style.css";
import { Card, Table, ButtonGroup, Button, Accordion } from "react-bootstrap";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faList, faTrash } from "@fortawesome/free-solid-svg-icons";

class MatchTrips extends Component {
  constructor(props) {
    super(props);

    this.state = {
      //tripId: this.props.match.params.tripId,
      //tripId: "6",
      trips: [
        {
          trip: {
            firstName: "Ram",
            tripDate: "2021/05/21",
            destination: "Shimla",
            tripId: 1,
          },
        },
        {
          trip: {
            firstName: "shyam",
            tripDate: "2021/05/21",
            destination: "Mathura",
            tripId: 2,
          },
        },
      ],
    };
  }

  // componentDidMount() {
  //   console.log(this.state.tripId);
  //   TripServices.GetmatchTrip(this.state.tripId).then((res) => {
  //     console.log(res.data);
  //     this.setState({
  //       trips: res.data,
  //     });
  //     console.log(this.state.trips[0].trip.tripId);
  //   });
  // }
  render() {
    const { trips } = this.state;
    return (
      <div>
        <Card className={"border border-dark bg-dark text-white"}>
          <Card.Header>
            <div style={{ float: "left" }}>
              <FontAwesomeIcon icon={faList} /> Trip List
            </div>
          </Card.Header>
          <Card.Body>
            <Accordion defaultActiveKey="0">
              <Table bordered hover striped variant="dark">
                <thead>
                  <tr>
                    <th>Name</th>
                    <th>Travel Date</th>
                    <th>Destination</th>
                    <th>Actions</th>
                  </tr>
                </thead>

                <tbody>
                  {trips.length === 0 ? (
                    <tr align="center">
                      <td colSpan="7">No Trips Available.</td>
                    </tr>
                  ) : (
                    trips.map((mtrip) => (
                      <Card className={"border border-dark bg-dark text-white"}>
                        <tr key={mtrip.trip.tripId}>
                          <Card.Header>
                            <td>{mtrip.trip.firstName}</td>
                            <td>{mtrip.trip.tripDate}</td>
                            <td>{mtrip.trip.destination}</td>

                            <td>
                              {/* <ButtonGroup>
                                <Button
                                  size="sm"
                                  variant="outline-danger"
                                  //onClick={this.deleteBook.bind(this, book.id)}
                                >
                                  <FontAwesomeIcon icon={faTrash} />
                                  Delete
                                </Button>
                              </ButtonGroup> */}
                              <Accordion.Toggle
                                as={Button}
                                variant="link"
                                eventKey={mtrip.trip.tripId}
                              >
                                View
                              </Accordion.Toggle>
                            </td>
                          </Card.Header>
                        </tr>

                        <Accordion.Collapse eventKey={mtrip.trip.tripId}>
                          <Card.Body>Hello! I'm the body</Card.Body>
                        </Accordion.Collapse>
                      </Card>
                    ))
                  )}
                </tbody>
              </Table>
            </Accordion>
          </Card.Body>
        </Card>
      </div>
    );
  }
}

export default MatchTrips;
