import React, { Component } from "react";

class ChangePhoto extends Component {
  constructor(props) {
    super(props);
    this.state = {
      //file: localStorage.getItem("photoPath"),
      imgUpload: "",
    };
    //console.log(this.state.file);
    // localStorage.removeItem("photoPath");

    // this.handleChange = this.handleChange.bind(this);
    this.backtoDashboardService = this.backtoDashboardService.bind(this);
    this.changePhoto = this.changePhoto.bind(this);
    this.getBase64 = this.getBase64.bind(this);
  }

  // handleChange(event) {
  //   console.log(event.target.files[0]);
  //   this.setState({
  //     file: URL.createObjectURL(event.target.files[0]),
  //   });
  // }

  changePhoto() {
    console.log(this.state.imgUpload);
  }
  getBase64(e) {
    var file = e.target.files[0];
    let reader = new FileReader();
    reader.readAsDataURL(file);
    reader.onload = () => {
      this.setState({
        imgUpload: reader.result,
      });
    };
    reader.onerror = function (error) {
      console.log("Error: ", error);
    };
  }
  backtoDashboardService() {
    return this.props.history.push("/dashboard");
  }
  render() {
    return (
      <div>
        {" "}
        <br></br>
        <div className="card col-md-6 offset-md-3">
          <br />
          <br />
          <h3 className="text-center text-dark"> Change Your Profile Photo</h3>
          <div className="card-body text-center">
            <input
              type="file"
              onChange={this.getBase64}
              className="input-file"
              name="imgUpload"
              accept=".png"
            />
            <br />
            <br />
            <img
              src={this.state.imgUpload}
              className="rounded-circle"
              width="150"
            />
            <br />
            <br />
            <button
              style={{ marginLeft: "10px" }}
              onClick={this.changePhoto}
              className="btn btn-info"
            >
              Change Photo{" "}
            </button>
            <button
              style={{ marginLeft: "10px" }}
              onClick={this.backtoDashboardService}
              className="btn btn-danger"
            >
              Cancel{" "}
            </button>
          </div>
        </div>
      </div>
    );
  }
}

export default ChangePhoto;
