import { Button, Card } from "react-bootstrap";
import React, { Component } from "react";
import TextField from "@material-ui/core/TextField";
import ChatServices from "../services/ChatServices";
// import { Card } from "material-ui";

class ChatUI extends Component {
  constructor(props) {
    super(props);
    this.state = {
      currUserId: "4",
      otherUserid: "",
      otherUserName: "",
      message: {
        toUserId: "",
        message: "",
      },
      chatUsers: [
        {
          id: "1",
          firstName: "Johnny",
          lastName: "Depp",
          email: "j@d.com",
          password: "123",
          gender: "male",
          dob: "22/11/1997",
          description: "iiitb",
          // photoPath: "",
        },
        {
          id: "2",
          firstName: "Leonardo",
          lastName: "Dicaprio",
          email: "l@d.com",
          password: "123",
          gender: "male",
          dob: "12/34/1975",
          description: "iiitb",
          // photoPath: "",
        },
        {
          id: "3",
          firstName: "Dwight",
          lastName: "Shrute",
          email: "j@d.com",
          password: "123",
          gender: "male",
          dob: "22/11/1997",
          description: "iiitb",
          // photoPath: "",
        },
      ],
      chatMessages: [
        {
          fromUser: "3",
          toUser: "4",
          message: "hi",
          msgTimestamp: "1",
        },
        {
          fromUser: "4",
          toUser: "3",
          message: "yo",
          msgTimestamp: "2",
        },
        {
          fromUser: "5",
          toUser: "3",
          message: "hey",
          msgTimestamp: "3",
        },
      ],
    };
    this.showUserChat = this.showUserChat.bind(this);
  }

  componentDidMount() {
    ChatServices.getChatUsers().then((res) => {
      console.log(res.data);
      this.setState({
        chatUsers: res.data,
      });
    });
  }

  showUserChat = (chatUser) => {
    var name = chatUser.firstName + " " + chatUser.lastName;
    var id = chatUser.id;
    this.setState({ otherUserName: name }, () => {
      console.log(this.state.otherUserName);
      document.getElementById("otherUserName").innerHTML = name;
      this.setState((prevState) => ({
        message: {
          ...prevState.message,
          toUserId: id,
        },
      }));
      // send get
      ChatServices.getChats(chatUser.id).then((res) => {
        this.setState({
          chatMessages: res.data,
        });
      });
    });
    // this.setState({
    //   chatMessages: [
    //     {
    //       fromUser: "3",
    //       toUser: "4",
    //       message: "namaste",
    //       msgTimestamp: "1",
    //     },
    //     {
    //       fromUser: "4",
    //       toUser: "3",
    //       message: "namaskar",
    //       msgTimestamp: "2",
    //     },
    //     {
    //       fromUser: "5",
    //       toUser: "3",
    //       message: "pranam",
    //       msgTimestamp: "2",
    //     },
    //   ],
    // });
  };

  handleChange = (value) => {
    this.setState(() => ({
      messageobj: {
        toUserId: this.props.trip.user.id,
        message: value,
      },
    }));
  };

  sendMsg = () => {
    console.log(this.state.message);
    if (this.state.message.message === "") {
      return;
    }
    let newmessage = this.state.message;
    console.log(this.state.message.message);
    ChatServices.sendMessage(newmessage).then((res) => {
      console.log(res.data);
      this.props.history.push("/");
    });

    // this.setState((prevState) => ({
    //   message: {
    //     ...prevState.message,
    //     message: "",
    //   },
    // }));
  };

  render() {
    const { chatUsers, chatMessages } = this.state;
    return (
      <div>
        <div className="container">
          <div className="main-body">
            <div className="row gutters-sm">
              <div className="col-md-5 mb-3">
                <div className="card">
                  <div className="card-body">
                    <div className="d-flex flex-column align-items-center text-center">
                      <div className="mt-3">
                        <h4 className="mb-4">My Messages</h4>
                        <p className="text-secondary mb-4 ">
                          People who tried to contact you-{" "}
                        </p>
                      </div>
                    </div>
                    {chatUsers.map((chatUser) => (
                      <div key={chatUser.id}>
                        <div className="row">
                          <div className="col-sm-8">
                            <h6 className="mb-0">
                              <i className="material-icons text-info mr-2">
                                {chatUser.firstName} {chatUser.lastName}
                              </i>
                            </h6>
                          </div>
                          <Button
                            variant="primary"
                            size="sm"
                            onClick={() => {
                              this.showUserChat(chatUser);
                            }}
                          >
                            Messages
                          </Button>
                        </div>
                        <hr></hr>
                      </div>
                    ))}
                  </div>
                </div>
              </div>

              <div className="col-md-7">
                <div className="card mb-6">
                  <div className="card-body" id="chatsdisplay">
                    <div className="row">
                      <div className="col-lg-8">
                        <h5 className="mb-4">
                          <i
                            className="material-icons text-info mr-2"
                            id="otherUserName"
                          ></i>
                        </h5>
                      </div>
                    </div>
                    {chatMessages.map((message) => (
                      <div key={message.msgTimestamp}>
                        {this.state.currUserId === message.fromUser ? (
                          <div className="row">
                            {" "}
                            <div className="col-6"> </div>
                            <div className="col-6 text-right">
                              <Card bg="info" text="light">
                                <Card.Body>{message.message}</Card.Body>
                              </Card>
                              <h6 className="mb-0 col-sm text-secondary">
                                You{" "}
                              </h6>
                            </div>
                          </div>
                        ) : (
                          <div className="row">
                            {" "}
                            <div className="col-6 text-left">
                              <Card bg="primary" text="light">
                                <Card.Body>{message.message}</Card.Body>
                              </Card>
                              <h6 className="mb-0 col-sm text-secondary">
                                User{" "}
                              </h6>
                            </div>
                            <div className="col-6 text-right"></div>
                          </div>
                        )}
                      </div>
                    ))}
                    <hr></hr>
                    <div className="row">
                      <div className="col">
                        <TextField
                          autoFocus
                          margin="dense"
                          id="outlined-multiline-static"
                          placeholder="Message "
                          label="Write a message here"
                          multiline
                          rows={1}
                          variant="outlined"
                          fullWidth
                          onChange={(event) => this.handleChange()}
                          name="message"
                        />
                      </div>
                    </div>
                    <div className="text-right">
                      <Button
                        variant="primary"
                        size="sm"
                        onClick={this.sendMsg}
                      >
                        Send
                      </Button>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default ChatUI;
